.yui3-modal-mask{position:fixed;top:0;left:0;width:100%;height:100%}.yui3-modal-mask-content{position:absolute;top:0;left:0;width:100%;height:110%}.yui3-modal-mask-hidden{display:none}.yui3-modal-login{width:970px;height:540px;background-color:#FDFDFD;-moz-border-radius:4px;-webkit-border-radius:4px;-o-border-radius:4px;-ms-border-radius:4px;-khtml-border-radius:4px;border-radius:4px;padding:10px;background-color:#acacac;background-image:-webkit-gradient(linear, 180deg, 180deg, color-stop(0%, #acacac), color-stop(50%, #fcfcfc), color-stop(100%, #acacac));background-image:-webkit-linear-gradient(180deg, #acacac, #fcfcfc, #acacac);background-image:-moz-linear-gradient(180deg, #acacac, #fcfcfc, #acacac);background-image:-o-linear-gradient(180deg, #acacac, #fcfcfc, #acacac);background-image:-ms-linear-gradient(180deg, #acacac, #fcfcfc, #acacac);background-image:linear-gradient(180deg, #acacac, #fcfcfc, #acacac)}.yui3-modal-login .yui3-modal-login-content{width:100%;height:100%;background-color:#E7E7E7;-moz-border-radius:4px;-webkit-border-radius:4px;-o-border-radius:4px;-ms-border-radius:4px;-khtml-border-radius:4px;border-radius:4px;padding:1px;margin:-1px 0px 0px -1px;position:relative}.yui3-modal-login .yui3-modal-login-content .yui3-modal-login-close{position:absolute;top:-22px;right:-20px;display:block;width:20px;height:19px;padding-top:1px;text-align:center;background-color:#FEFFFE;color:#424242;cursor:pointer;-moz-border-radius:4px;-webkit-border-radius:4px;-o-border-radius:4px;-ms-border-radius:4px;-khtml-border-radius:4px;border-radius:4px;font:bold 18px/100% arial}.yui3-modal-login .yui3-modal-login-content .yui3-modal-login-iframe-wrapper{width:inherit;height:inherit;overflow:hidden;-webkit-overflow-scrolling:touch}.yui3-modal-login .yui3-modal-login-content .yui3-modal-login-iframe-wrapper .yui3-modal-login-iframe{border:none;width:100%;height:100%}